import React from "react";
import { NavLink } from "react-router-dom";
import "./ComponentSearch.css";
import { Table } from "antd";
import TableContainer from "./ContainerTable";
import { useState } from "react";
import { useEffect } from "react";

import { SettingOutlined } from "@ant-design/icons";
import { Button, Cascader, Input, Select, Space } from "antd";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";

const { Option } = Select;

export default function ComponentSearch() {
  const [hideTable, setHideTable] = useState(false);

  const [valueSection, setValueSection] = useState("");
  const [valueInput, setValueInput] = useState("");

  const handleGetValue = (e) => {
    setValueSection(e.target.value);
    setValueInput(e.target.value);
  };

  const showData = ["Trường", "Ngành", "Điểm"];

  const selectBefore = (
    <Select
      defaultValue="Chọn tổ hợp"
      className="select-after"
      value={valueSection}
    >
      <Option value="A00">A00</Option>
      <Option value="A01">A01</Option>
      <Option value="B00">B00</Option>
      <Option value="C00">C00</Option>
      <Option value="C01">C01</Option>
      <Option value="D01">D01</Option>
      <Option value="D07">D07</Option>
    </Select>
  );

  return (
    <>
      <div className="form-import-container">
        <div className="form-import-content">
          <div className="form-import">
            <div className="title-content">
              <div className="title">
                <h2>
                  {" "}
                  ĐIỂM CHUẨN CÁC TRƯỜNG ĐẠI HỌC 2021 VÀ GỢI Ý ĐĂNG KÝ NGUYỆN
                  VỌNG
                </h2>
              </div>
              <div className="body-text">
                <div className="body-text-router">
                  <NavLink
                    className="effect-hover"
                    to="/"
                    style={{ color: "#fff" }}
                  >
                    Điểm chuẩn các trường đại học
                  </NavLink>
                  <NavLink
                    className="active-router effect-hover"
                    to="/FormSearch"
                    style={{ color: "#fff" }}
                  >
                    Tra cứu và lựa chọn ngành
                  </NavLink>
                </div>
                <div className="body-text-input">
                  <p style={{ marginTop: "35px" }}>
                    Chú ý: dấu (*) là bắt buộc nhập
                  </p>

                  <div style={{ textAlign: "center", marginTop: "5%" }}>
                    <Space>
                      <Input
                        onChange={handleGetValue}
                        value={valueInput}
                        addonBefore={selectBefore}
                        placeholder={"Nhập điểm của bạn...."}
                      />
                      <Button>Tìm Kiếm</Button>
                    </Space>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        {hideTable ? (
          <div className="main">
            <p
              style={{
                fontWeight: "800",
                position: "fixed",
                width: "1180px",
                textAlign: "center",
                marginBottom: "25px",
                top: "23%",
                fontSize: "22px",
              }}
            >
              BẢNG KẾT QUẢ
            </p>
            <div className="showData-list">
              <ul className="list-name-header-container">
                {/* {showData.map((item, index) => {
                  return (
                    <li className="list-name-header show-content" key={index}>
                      {item}
                    </li>
                  );
                })} */}
              </ul>
              {/* {typeof studentData !== "string" && studentData ? (
                studentData?.map((dataShowColum, index) => {
                  return (
                    <ul className="show-Data" key={index}>
                      {Object.keys(dataShowColum)?.map((key, index) => (
                        <li className="data-sudent show-content" key={index}>
                          <p key={index}> {dataShowColum[key]}</p>
                        </li>
                      ))}
                    </ul>
                  );
                })
              ) : (
                <p
                  style={{
                    width: "1180px",
                    zIndex: "100",
                    marginTop: "60px",
                    textAlign: "center",
                    position: "fixed",
                    fontWeight: "800",
                    fontSize: "18px",
                  }}
                >
                  {studentData}
                </p>
              )} */}

              <div className="close-data-form">
                <button type="button" className="btn btn-primary">
                  Đóng bảng
                </button>
              </div>
            </div>
          </div>
        ) : null}
      </div>
    </>
  );
}
