import "./ComponentImport.css";
import { XLSX$Utils } from "xlsx";
import xlsx from "xlsx";

export class ComponentImport extends Component {
  render() {
    const fileData = {};

    const fileSelect = (e) => {
      const files = e.target.files;
      fileData["fileName"] = files[0].name;

      if (typeof FileList != "undefined") {
        const reader = new FileReader();
        if (reader.readAsBinaryString) {
          reader.onload = function (e) {
            processExcelFile(e.target.result);
          };
        }
      } else {
        reader.onload = function (e) {
          const data = "";
          const bytes = new Uint8Array(e.target.result);
          for (var i = 0; i < bytes.byteLength; i++) {
            data += String.fromCharCode(bytes[i]);
          }
          processExcelFile(data);
        };
      }
      console.log(files);
    };

    return (
      <>
        <div className="content">
          <input type="file" onChange={fileSelect}></input>
        </div>
      </>
    );
  }
}

export default ComponentImport;
